                        </div>

                        <div class="flex items-center p-6 space-x-2 border-t border-gray-200 rounded-b dark:border-gray-600">
                          <a href={`https://api.whatsapp.com/send?phone=${item.Contact}`}>
                            <button
                              data-modal-hide="defaultModal"
                              type="button"
                              class="text-black bg-lime-600 hover:bg-lime-700 focus:ring-4 focus:outline-none focus:ring-blue-300 rounded-lg border border-gray-200 text-sm font-medium px-5 py-2.5 hover:text-gray-900 focus:z-10 dark:bg-gray-700 dark:text-gray-300 dark:border-gray-500 dark:hover:text-white dark:hover:bg-gray-600 dark:focus:ring-gray-600"
                            >
                              Chat
                            </button>
                          </a>
                          <button
                            onClick={() => setEditProfil(true)}
                            data-modal-hide="defaultModal"
                            type="button"
                            class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
                          >
                            Update
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </>
            )}
          </td>
        ))}

        <td class="">
          {EditProfil ? (
            <>
              <div className="fixed z-10 inset-0 overflow-y-auto">
                <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
                  <div className="fixed inset-0 transition-opacity">
                    <div className="absolute inset-0 bg-slate-800 opacity-75"></div>
                  </div>

                  <span className="hidden sm:inline-block sm:align-middle sm:h-screen"></span>

                  <div
                    className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full"
                    role="dialog"
                    aria-modal="true"
                    aria-labelledby="modal-headline"
                  >
                    <div className="bg-white px-4 pt-1 pb-4 sm:p-6 sm:pb-4">
                      <div className="sm:flex sm:items-start">
                        <form className="w-full max-w-lg">
                          <div class="flex flex-wrap -mx-3 ">
                            <div class="w-full px-1">
                              <label
                                class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
                                for="grid-first-name"
                              >
                                Masukan Nama
                              </label>
                              <input
                                type="text"
                                class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                                name="Name"
                                required
                                onChange={(e) => {
                                  setName(e.target.value);
                                }}
                              ></input>
                            </div>
                          </div>
                          <div class="flex flex-wrap -mx-3 ">
                            <div class="w-full px-1">
                              <label
                                class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
                                for="grid-first-name"
                              >
                                Masukan Alamat
                              </label>
                              <input
                                type="text"
                                class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                                name="Addres"
                                required
                                onChange={(e) => {
                                  setAddres(e.target.value);
                                }}
                              ></input>
                            </div>
                          </div>
                          <div class="flex flex-wrap -mx-3 ">
                            <div class="w-full px-1">
                              <label
                                class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
                                for="grid-first-name"
                              >
                                Masukan Nomor Telephone
                              </label>
                              <input
                                type="Number"
                                class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                                name="Contact"
                                required
                                onChange={(e) => {
                                  setContact(e.target.value);
                                }}
                              ></input>
                            </div>
                          </div>
                          <div class="flex flex-wrap -mx-3 ">
                            <div class="w-full px-1">
                              <label
                                class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
                                for="grid-first-name"
                              >
                                Born Date
                              </label>
                              <input
                                type="Date"
                                class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                                name="Borndate"
                                required
                                onChange={(e) => {
                                  setBornDate(e.target.value);
                                }}
                              ></input>
                            </div>
                          </div>

                          <div class="flex flex-wrap -mx-3 ">
                            <div class="w-full px-1">
                              <label
                                class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-1"
                                for="grid-password"
                              >
                                Profil Picture
                              </label>
                              <input
                                class="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                                id="grid-password"
                                type="file"
                                placeholder=""
                                name="Picture"
                                onChange={handleFileChange}
                                required
                              />
                              <p class="text-gray-600 text-xs italic"></p>
                            </div>
                          </div>
                          <div className="flex justify-end">
                            <button
                              className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded mr-2"
                              onClick={() => setEditProfil(false)}
                            >
                              Batal
                            </button>
                            <button
                              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                              type="submit"
                              onClick={updateProfile}
                            >
                              Submit
                            </button>
                          </div>
                        </form>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </>
          ) : null}
        </td>
      </div>

    </main>
  );
};

export default ClassA;
