  };

  const updateWilliam = (data) => {
    setWilliam(data);
    setBackgroundColorWilliam(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
          ? "#C00000"
          : "#565454"
    );
  };

  const updateLucas = (data) => {
    setLucas(data);
    setBackgroundColorLucas(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
          ? "#C00000"
          : "#565454"
