  };

  const updateHenry = (data) => {
    setHenry(data);
    setBackgroundColorHenry(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
          ? "#C00000"
          : "#565454"
    );
  };

  // Fungsi untuk menghitung jumlah nilai "Present"
  const countPresent = () => {
    const variables = [
      chrisna,
      Charlotte,
      Anggi,
      Sophia,
      Emily,
      Violet,
      William,
      Lucas,
      Henry,
    ];
    let presentCount = 0;
    for (let i = 0; i < variables.length; i++) {
      if (variables[i] === "Present") {
        presentCount++;
      }
    }
    return presentCount;
  };

  useEffect(() => {
    fetch("http://localhost:3001/api/get/students")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
      })
      .catch((error) => {
        // Tangani error jika permintaan gagal
        console.error("Error fetching data:", error);
      });
  }, []);

  const updateProfile = () => {
    const data = new FormData();
    data.append("Name", Name);
    data.append("Addres", Addres);
    data.append("Contact", Contact);
    data.append("BornDate", BornDate);
    data.append("Picture", file);

    fetch(`http://192.168.101.236:3001/api/put/students/${No}`, {
      method: "PUT",
      body: data,
    })
      .then((response) => {
        if (response.status === 200) {
          alert("Data has been updated successfully");
          window.location.reload();
        } else {
          throw new Error("Error updating data");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // Handle file change
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };



  useEffect(() => {
    if (status !== "" && prevStatus !== status) {
      fetch("http://localhost:3001/api/post/data", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          status: prevStatus,
          mesin: mesin,
          line: line,
          timer: timer,
        }),
      })
        .then((response) => response.json())
        .then((data) => console.log(data))
        .catch((error) => console.error(error));
    }
    setPrevStatus(status);
  }, [status, mesin, line, timer, prevStatus]);

  return (
    <main className=" mb-3 ">
      <div
        style={{
          height: "200vh",
          background: "linear-gradient(90deg, #005A5B 50%, #007369 50%)",
        }}
      >
        <div className="items-center  mx-auto text-center justify-center pt-7 mb-10">
          <span className="font-semibold text-white text-2xl ">Class A</span>
        </div>


        <span></span>
        <div class="flex ">
          <button
            className="mx-auto p-5 hover:scale-90"
            onClick={() => {
              setNo(1);
              setOpenChrisna(true);
            }}
          >
            <svg
              width="100px"
              viewBox="0 0 1024 1024"
              class="icon"
              version="1.1"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                d="M768 544V64a64 64 0 0 0-64-64H320a64 64 0 0 0-64 64v480h512z"
                fill={backgroundColorChrisna}
              />
              <path d="M256 512h512v32H256z" fill="" />
              <path
                d="M608 160m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                fill=""
              />
              <path
                d="M512 288m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                fill=""
              />
              <path
                d="M416 160m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                fill=""
              />
              <path
                d="M608 416m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                fill=""
              />
              <path
                d="M416 416m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                fill=""
              />
              <path
                d="M256 992a32 32 0 1 0 64 0V672H256v320zM704 672v320a32 32 0 1 0 64 0V672h-64z"
                fill="#434854"
              />
              <path
                d="M832 640a32 32 0 0 1-32 32H224a32 32 0 0 1-32-32v-64a32 32 0 0 1 32-32h576a32 32 0 0 1 32 32v64z"
                fill={backgroundColorChrisna}
              />
              <path
                d="M800 544H224a32 32 0 0 0-32 32v32a32 32 0 0 1 32-32h576a32 32 0 0 1 32 32v-32a32 32 0 0 0-32-32z"
                fill="#FFFFFF"
              />
              <path d="M704 672h64v32h-64zM256 672h64v32H256z" fill="" />
            </svg>
            {data.map((item, index) => (
              <span class="font-thin text-white">
                {item.No === 1 && <>{item.Name}</>}
