import React, { useState, useEffect } from "react";
import firebase from "firebase/compat/app";
import "firebase/compat/database";

const firebaseConfig = {
  apiKey: "AIzaSyBF8Q3AuT5m-iBayXDt82r8RyADUdnyGsk",
  databaseURL:
    "https://classroom-1a392-default-rtdb.asia-southeast1.firebasedatabase.app",
};
firebase.initializeApp(firebaseConfig);

const database = firebase.database();

const ClassA = () => {
  const [chrisna, setchrisna] = useState("");
  const [backgroundColorChrisna, setBackgroundColorChrisna] = useState("");
  const [Charlotte, setCharlotte] = useState("");
  const [backgroundColorCharlotte, setBackgroundColorCharlotte] = useState("");
  const [Anggi, setAnggi] = useState("");
  const [backgroundColorAnggi, setBackgroundColorAnggi] = useState("");
  const [Sophia, setSophia] = useState("");
  const [backgroundColorSophia, setBackgroundColorSophia] = useState("");
  const [Emily, setEmily] = useState("");
  const [backgroundColorEmily, setBackgroundColorEmily] = useState("");
  const [Violet, setViolet] = useState("");
  const [backgroundColorViolet, setBackgroundColorViolet] = useState("");
  const [William, setWilliam] = useState("");
  const [backgroundColorWilliam, setBackgroundColorWilliam] = useState("");
  const [Lucas, setLucas] = useState("");
  const [backgroundColorLucas, setBackgroundColorLucas] = useState("");
  const [Henry, setHenry] = useState("");
  const [backgroundColorHenry, setBackgroundColorHenry] = useState("");
  const [selectedItem, setSelectedItem] = useState(null);
  const [data, setData] = useState([]);

  const [OpenChrisna, setOpenChrisna] = useState(null);
  const [OpenCharlotte, setOpenCharlotte] = useState(null);
  const [OpenAnggi, setOpenAnggi] = useState(null);
  const [OpenSophia, setOpenSophia] = useState(null);
  const [OpenEmily, setOpenEmily] = useState(null);

  const [EditProfil, setEditProfil] = useState(false);

  // SUBMIT FILE
  const [file, setFile] = useState(null);

  // Update
  const [No, setNo] = useState("");
  const [Name, setName] = useState("");
  const [Addres, setAddres] = useState("");
  const [Contact, setContact] = useState("");
  const [BornDate, setBornDate] = useState("");

  useEffect(() => {
    const ref1 = firebase.database().ref("ClassA/Chrisna");
    ref1.on("value", (snapshot) => {
      const data = snapshot.val();
      updateChrisna(data);
    });

    const ref2 = firebase.database().ref("ClassA/Charlotte");
    ref2.on("value", (snapshot) => {
      const data = snapshot.val();
      updateCharlotte(data);
    });

    const ref3 = firebase.database().ref("ClassA/Anggi");
    ref3.on("value", (snapshot) => {
      const data = snapshot.val();
      updateAnggi(data);
    });

    const ref4 = firebase.database().ref("ClassA/Sophia");
    ref4.on("value", (snapshot) => {
      const data = snapshot.val();
      updateSophia(data);
    });

    const ref5 = firebase.database().ref("ClassA/Emily");
    ref5.on("value", (snapshot) => {
      const data = snapshot.val();
      updateEmily(data);
    });

    const ref6 = firebase.database().ref("ClassA/Violet");
    ref6.on("value", (snapshot) => {
      const data = snapshot.val();
      updateViolet(data);
    });

    const ref7 = firebase.database().ref("ClassA/William");
    ref7.on("value", (snapshot) => {
      const data = snapshot.val();
      updateWilliam(data);
    });

    const ref8 = firebase.database().ref("ClassA/Lucas");
    ref8.on("value", (snapshot) => {
      const data = snapshot.val();
      updateLucas(data);
    });

    const ref9 = firebase.database().ref("ClassA/Henry");
    ref9.on("value", (snapshot) => {
      const data = snapshot.val();
      updateHenry(data);
    });
    return () => {};
  }, []);

  const updateChrisna = (data) => {
    setchrisna(data);
    setBackgroundColorChrisna(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateCharlotte = (data) => {
    setCharlotte(data);
    setBackgroundColorCharlotte(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateAnggi = (data) => {
    setAnggi(data);
    setBackgroundColorAnggi(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateSophia = (data) => {
    setSophia(data);
    setBackgroundColorSophia(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateViolet = (data) => {
    setViolet(data);
    setBackgroundColorViolet(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateEmily = (data) => {
    setEmily(data);
    setBackgroundColorEmily(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateWilliam = (data) => {
    setWilliam(data);
    setBackgroundColorWilliam(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateLucas = (data) => {
    setLucas(data);
    setBackgroundColorLucas(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateHenry = (data) => {
    setHenry(data);
    setBackgroundColorHenry(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  // Fungsi untuk menghitung jumlah nilai "Present"
  const countPresent = () => {
    const variables = [
      chrisna,
      Charlotte,
      Anggi,
      Sophia,
      Emily,
      Violet,
      William,
      Lucas,
      Henry,
    ];
    let presentCount = 0;
    for (let i = 0; i < variables.length; i++) {
      if (variables[i] === "Present") {
        presentCount++;
      }
    }
    return presentCount;
  };

  useEffect(() => {
    fetch("http://localhost:3001/api/get/students")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
      })
      .catch((error) => {
        // Tangani error jika permintaan gagal
        console.error("Error fetching data:", error);
      });
  }, []);

  const updateProfile = () => {
    const data = new FormData();
    data.append("Name", Name);
    data.append("Addres", Addres);
    data.append("Contact", Contact);
    data.append("BornDate", BornDate);
    data.append("Picture", file);

    fetch(`http://192.168.101.236:3001/api/put/students/${No}`, {
      method: "PUT",
      body: data,
    })
      .then((response) => {
        if (response.status === 200) {
          alert("Data has been updated successfully");
          window.location.reload();
        } else {
          throw new Error("Error updating data");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // Handle file change
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  return (
    <main className=" mb-3 ">
        <div
          style={{
            height: "200vh",
            background: "linear-gradient(90deg, #005A5B 50%, #007369 50%)",
          }}
        >
