  };

  const updateLucas = (data) => {
    setLucas(data);
    setBackgroundColorLucas(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
          ? "#C00000"
          : "#565454"
    );
  };

  const updateHenry = (data) => {
    setHenry(data);
    setBackgroundColorHenry(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
          ? "#C00000"
          : "#565454"
    );
  };

  // Fungsi untuk menghitung jumlah nilai "Present"
  const countPresent = () => {
    const variables = [
      Student1,
      Charlotte,
      Anggi,
      Sophia,
      Emily,
      Violet,
      William,
      Lucas,
      Henry,
    ];
    let presentCount = 0;
    for (let i = 0; i < variables.length; i++) {
      if (variables[i] === "Present") {
        presentCount++;
      }
    }
    return presentCount;
  };

  useEffect(() => {
    fetch("http://localhost:3001/api/get/students")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
      })
      .catch((error) => {
        // Tangani error jika permintaan gagal
        console.error("Error fetching data:", error);
      });
  }, []);

  const updateProfile = () => {
    const data = new FormData();
    data.append("Name", Name);
    data.append("Addres", Addres);
    data.append("Contact", Contact);
    data.append("BornDate", BornDate);
    data.append("Picture", file);

    fetch(`http://192.168.101.236:3001/api/put/students/${No}`, {
      method: "PUT",
      body: data,
    })
      .then((response) => {
        if (response.status === 200) {
          alert("Data has been updated successfully");
          window.location.reload();
        } else {
          throw new Error("Error updating data");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // Handle file change
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };


   // Function to handle data posting to backend when Student1 changes
   useEffect(() => {
    if (Student1 !== prevStudent1) {
      postStudentData();
    }
  }, [Student1]);

  const postStudentData = async () => {
    try {
      const response = await fetch("http://192.168.101.236:3001/api/post/data", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ Student1, Name1 }),
      });

      if (!response.ok) {
        throw new Error("Failed to post data");
      }

      console.log("Data posted successfully!");
      setPrevStudent1(Student1);
    } catch (error) {
      console.error("Error posting data:", error);
    }
  };


  // useEffect(() => {
  //   if (Student1 !== "" && prevStudent1 !== Student1) {
