import React, { useState, useEffect } from "react";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";

const PPICProd = () => {
  const [isWeek1, setIsWeek1] = useState(true);
  const [isDelet, setIsDelet] = useState(false);
  const [isWip, setIsWip] = useState(false);
  const [isWeek2, setIsWeek2] = useState(false);
  const [isModelW1, setIsModelW1] = useState(false);
  const [Model, setModel] = useState("");
  const [Pnc, setPnc] = useState("");
  const [Week, setWeek] = useState("");
  const [Stock, setStock] = useState("");
  const [SE, setSE] = useState("");
  const [date, setDate] = useState("");
  const [Wip, setWip] = useState("");
  const [QC, setQC] = useState("");
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [selectedItemWip, setSelectedItemWip] = useState(null);

  const [initialWip, setinitialWip] = useState(0);

  const exportToPDF = () => {
    const doc = new jsPDF();
    const table = document.getElementById("data-table");

    // Menambahkan tema "plain"

    doc.autoTable({ html: table, theme: "grid" });
    doc.save("PPIC.pdf");
  };

  const handleDateChange = (e) => {
    const inputDateTime = e.target.value;
    const formattedDateTime = inputDateTime.replace(
      /(\d{2})\/(\d{2})\/(\d{4})\s(\d{2}):(\d{2})/,
      "$3-$2-$1 $4:$5:00"
    );
    setDate(formattedDateTime);
  };

  useEffect(() => {
    fetch("http://10.14.81.43:3001/api/get/Week1")
      .then((response) => response.json())
      .then((json) => {
        // mengubah properti timestamp menjadi tanggal dan Date
        json.forEach((item) => {
          const date = new Date(item.Date);
          const day = date.getDate();
          const month = date.getMonth() + 1;
          const year = date.getFullYear();
          const hours = date.getHours();
          const minutes = date.getMinutes();
          const formattedDate = `${day.toString().padStart(2, "0")}-${month
            .toString()
            .padStart(2, "0")}-${year} / ${hours
            .toString()
            .padStart(2, "0")}:${minutes.toString().padStart(2, "0")}`;
          item.Date = formattedDate;
        });
        json.sort((a, b) => Date.parse(a.Date) - Date.parse(b.Date));
        json.reverse();
        setData(json);
        setFilteredData(json);
      });
  }, []);

  // Submit Network
  const SubmitPPIC = (event) => {
    if (!Model || !Pnc || !Week || !date || !Stock) {
      alert("Harap isi semua kolom!");
      return;
    }

    const data = {
      Model: Model,
      Pnc: Pnc,
      Week: Week,
      Stock: Stock,
      Wip: Wip,
      date: date,
      SE: SE,
      QC: QC,
    };

    fetch(`http://10.14.81.43:3001/api/post/Week1`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => {
        if (response.status === 200) {
          alert("Data Berhasil Di Input");
          setIsModelW1(false);
          window.location.reload();
          event.preventDefault();
        } else {
          throw new Error("Error adding data");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    if (Stock !== "" && Week !== "") {
      const SEValue = parseInt(Stock) - parseInt(Week);
      setSE(SEValue.toString());
    } else {
      setSE("");
    }
  }, [Week, Stock]);

  const handleDelete = () => {
    if (selectedItem) {
      // Kirim permintaan ke backend untuk menghapus data dengan ID selectedItem
      fetch(`http://10.14.81.43:3001/api/delete/${selectedItem}`, {
        method: "DELETE",
      })
        .then((response) => response.json())
        .then((result) => {
          alert("Data Berhasil Di Hapus");
          window.location.reload();
          setSelectedItem(null);
        })
        .catch((error) => {
          console.error("Error deleting data:", error);
        });
    }
  };

  const UpdatePPIC = (event) => {
    const data = {
      Wip: parseInt(Wip),
    };
    fetch(
      `http://10.14.81.43:3001/api/put/Week1PPIC/${selectedItemWip.No}`,
      {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      }
    )
      .then((response) => {
        if (response.status === 200) {
          alert("Rt");
          window.location.reload();
          event.preventDefault();
        } else {
          throw new Error("Error updating data");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    if (selectedItem) {
      setinitialWip(selectedItemWip.Wip);
      setWip(selectedItemWip.Wip);
    }
  }, [selectedItemWip]);

  useEffect(() => {
    // Mengupdate nilai Wip dengan menambahkan nilai Wip
    const newWip = initialWip + parseInt(Wip);
    setWip(newWip);
  }, [initialWip, Wip]);

  return (
    <body
      className="h-full w-full"
      style={{
        height: "200vh",
        background: "linear-gradient(90deg, #005A5B 50%, #007369 50%)",
      }}
    >
      <button
        data-drawer-target="logo-sidebar"
        data-drawer-toggle="logo-sidebar"
        aria-controls="logo-sidebar"
        // onClick={toggleDrawer}
        type="button"
        class="inline-flex items-center p-2 mt-2 ml-3 text-sm text-gray-500 rounded-lg md:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200 dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600"
      >
        <span class="sr-only">Open sidebar</span>
        <svg
          class="w-6 h-6"
          aria-hidden="true"
          fill="currentColor"
          viewBox="0 0 20 20"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            clip-rule="evenodd"
            fill-rule="evenodd"
            d="M2 4.75A.75.75 0 012.75 4h14.5a.75.75 0 010 1.5H2.75A.75.75 0 012 4.75zm0 10.5a.75.75 0 01.75-.75h7.5a.75.75 0 010 1.5h-7.5a.75.75 0 01-.75-.75zM2 10a.75.75 0 01.75-.75h14.5a.75.75 0 010 1.5H2.75A.75.75 0 012 10z"
          ></path>
        </svg>
      </button>

      <aside
        id="logo-sidebar"
        class="fixed top-0 left-0 z-40 w-44 h-screen transition-transform -translate-x-full  sm:translate-x-0"
        aria-label="Sidebar"
      >
        <div class="h-full px-3 py-4 overflow-y-auto bg-gray-50 dark:bg-gray-800">
          <a href="/Pin" class="flex items-center pl-2.5 mb-5">
            <img
              src={process.env.PUBLIC_URL + "/AVI.png"}
              class="h-6 mr-3 sm:h-7"
              alt="Flowbite Logo"
            />
          </a>
          <ul class="space-y-2">
            <li>
              <button
                onClick={() => {
                  setIsModelW1(false);
                  setIsWeek1(true);
                  setIsWeek2(false);
                }}
                class="flex items-center p-2 text-base font-normal text-gray-900 rounded-lg dark:text-white hover:bg-gray-100 dark:hover:bg-gray-700"
              >
                <svg
                  aria-hidden="true"
                  class="flex-shrink-0 w-6 h-6 text-gray-500 transition duration-75 dark:text-gray-400 group-hover:text-gray-900 dark:group-hover:text-white"
                  fill="currentColor"
                  viewBox="0 0 20 20"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path d="M5 3a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2V5a2 2 0 00-2-2H5zM5 11a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2v-2a2 2 0 00-2-2H5zM11 5a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V5zM11 13a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z"></path>
                </svg>
                <span class="flex-1 ml-3 whitespace-nowrap">Week 1</span>
              </button>
            </li>
            <li>
              <button
                onClick={() => {
                  setIsModelW1(false);
                  setIsWeek1(false);
                  setIsWeek2(true);
                }}
                class="flex items-center p-2 text-base font-normal text-gray-900 rounded-lg dark:text-white hover:bg-gray-100 dark:hover:bg-gray-700"
              >
                <svg
                  aria-hidden="true"
                  class="flex-shrink-0 w-6 h-6 text-gray-500 transition duration-75 dark:text-gray-400 group-hover:text-gray-900 dark:group-hover:text-white"
                  fill="currentColor"
                  viewBox="0 0 20 20"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path d="M5 3a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2V5a2 2 0 00-2-2H5zM5 11a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2v-2a2 2 0 00-2-2H5zM11 5a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V5zM11 13a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z"></path>
                </svg>
                <span class="flex-1 ml-3 whitespace-nowrap">Week 2</span>
              </button>
            </li>
            <li>
              <button class="flex items-center p-2 text-base font-normal text-gray-900 rounded-lg dark:text-white hover:bg-gray-100 dark:hover:bg-gray-700">
                <svg
                  aria-hidden="true"
                  class="flex-shrink-0 w-6 h-6 text-gray-500 transition duration-75 dark:text-gray-400 group-hover:text-gray-900 dark:group-hover:text-white"
                  fill="currentColor"
                  viewBox="0 0 20 20"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path d="M5 3a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2V5a2 2 0 00-2-2H5zM5 11a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2v-2a2 2 0 00-2-2H5zM11 5a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V5zM11 13a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z"></path>
                </svg>
                <span class="flex-1 ml-3 whitespace-nowrap">Week 3</span>
              </button>
            </li>
            <li>
              <button class="flex items-center p-2 text-base font-normal text-gray-900 rounded-lg dark:text-white hover:bg-gray-100 dark:hover:bg-gray-700">
                <svg
                  aria-hidden="true"
                  class="flex-shrink-0 w-6 h-6 text-gray-500 transition duration-75 dark:text-gray-400 group-hover:text-gray-900 dark:group-hover:text-white"
                  fill="currentColor"
                  viewBox="0 0 20 20"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path d="M5 3a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2V5a2 2 0 00-2-2H5zM5 11a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2v-2a2 2 0 00-2-2H5zM11 5a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V5zM11 13a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z"></path>
                </svg>
                <span class="flex-1 ml-3 whitespace-nowrap">Week 4</span>
              </button>
            </li>
          </ul>
        </div>
      </aside>

      {/*  */}
      <main>
        {isWeek1 ? (
          <>
            <div className="fixed z-10 inset-0 overflow-y-auto">
              <div className="flex items-end justify-center min-h-screen w-[880px]  pt-4 px-4 ml-48 md:ml-48 sm:ml-56 lg:ml-80 pb-0 text-center sm:block sm:p-0">
                <div className="fixed inset-0 transition-opacity">
                  <div className="absolute bg-gray-500 opacity-75"></div>
                </div>
                <span className="hidden sm:inline-block sm:align-middle sm:h-screen"></span>
                <div
                  className="inline-block  align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-5xl sm:w-full"
                  role="dialog"
                  aria-modal="true"
                  aria-labelledby="modal-headline"
                >
                  <section class="py-1 bg-[#889C9B]">
                    <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4 justify-center ml-2 mt-10">
                      <div className="relative flex flex-col min-w-0 break-words bg-white w-[830px] h-96  mb-6 shadow-lg rounded">
                        <div className="rounded-t mb-0 px-4 py-3 border-0">
                          <div className="flex flex-wrap items-center overflow-auto">
                            <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                              <h3 className="font-semibold text-base text-blueGray-700">
                                Week 1 PPIC
                              </h3>
                            </div>
                            <div className="relative w-full px-4 max-w-full flex-grow flex-1 text-right">
                              <button
                                className="bg-blue-400 text-white active:bg-blue-400  text-xs font-bold uppercase px-3 py-1 rounded outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                                type="button"
                                onClick={() => setIsWip(true)}
                              >
                                WIP
                              </button>
                              <button
                                className="bg-green-500 text-white active:bg-green-600 text-xs font-bold uppercase px-3 py-1 rounded outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                                type="button"
                                onClick={() => setIsModelW1(true)}
                              >
                                Add
                              </button>
                              <button
                                className="bg-red-500 text-white active:bg-red-500  text-xs font-bold uppercase px-3 py-1 rounded outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                                type="button"
                                onClick={exportToPDF}
                              >
                                PDF
                              </button>
                              <button
                                className="bg-red-900 text-white active:bg-red-900  text-xs font-bold uppercase px-3 py-1 rounded outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                                type="button"
                                onClick={() => setIsDelet(true)}
                              >
                                Hapus
                              </button>
                              {isModelW1 ? (
                                <>
                                  <div
                                    class="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity"
                                    aria-labelledby="modal-title"
                                    role="dialog"
                                    aria-modal="true"
                                    style={{ zIndex: 50 }}
                                  >
                                    <div class="fixed inset-0 z-50 P overflow-y-auto">
                                      <div class="flex items-end justify-center text-center sm:items-center sm:p-0">
                                        <div class="relative transform overflow-hidden rounded-lg bg-white text-left shadow-xl transition-all sm:my-8 sm:w-full sm:max-w-lg">
                                          <div class="bg-white px-4 pt-5 sm:p-6 ">
                                            <div className="sm:flex items-center justify-center sm:items-start">
                                              <div className="mx-auto flex h-12 w-12 flex-shrink-0 items-center justify-center rounded-full bg-slate-300 sm:mx-0 sm:h-10 sm:w-10">
                                                <svg
                                                  width="500px"
                                                  height="500px"
                                                  viewBox="0 0 1024 1024"
                                                  class="icon"
                                                  version="1.1"
                                                  xmlns="http://www.w3.org/2000/svg"
                                                >
                                                  <path
                                                    d="M755.2 390.3c0-11-9-20-20-20H286.5c-11 0-20 9-20 20s9 20 20 20h448.7c11 0 20-9 20-20zM679.5 527.1c0-11-9-20-20-20h-373c-11 0-20 9-20 20s9 20 20 20h372.9c11.1 0 20.1-9 20.1-20zM286.5 645.6c-11 0-20 9-20 20s9 20 20 20H529c11 0 20-9 20-20s-9-20-20-20H286.5z"
                                                    fill="#666666"
                                                  />
                                                  <path
                                                    d="M367.8 920.6h-141v-818H800v293c0 11 9 20 20 20s20-9 20-20V94.4c0-17.5-14.3-31.8-31.9-31.8H218.6c-17.6 0-31.9 14.3-31.9 31.8v834.4c0 17.5 14.3 31.8 31.9 31.8h149.1c11 0 20-9 20-20s-8.9-20-19.9-20zM820 790.7c-11 0-20 9-20 20v109.8H691.3c-11 0-20 9-20 20s9 20 20 20h116.9c17.6 0 31.9-14.3 31.9-31.9v-118c-0.1-10.9-9-19.9-20.1-19.9zM943.8 532.4l-40.4-39.7c-4.6-4.3-9.9-7.6-15.7-10s-11.8-3.6-17.7-3.6c-6 0-12 1.2-18 3.6s-11.1 5.7-15.5 10L581.3 742.6l106.6 106.6 255.8-250.7c9.2-9.1 13.9-20.1 13.9-33.1 0-12.9-4.6-23.9-13.8-33zM510.7 811.8L472.2 938l-6.1 18.8L620 915.7l38.6-37.7L552 771.4z"
                                                    fill="#666666"
                                                  />
                                                  <path
                                                    d="M726.5 298.4h-429c-14.8 0-26.8-12-26.8-26.8v-81.4c0-14.8 12-26.8 26.8-26.8h429c14.8 0 26.8 12 26.8 26.8v81.4c0 14.8-12 26.8-26.8 26.8z"
                                                    fill="#CCCCCC"
                                                  />
                                                </svg>
                                              </div>
                                            </div>
                                            <form>
                                              <div class="mb-2">
                                                <label
                                                  for="Model"
                                                  class="block text-gray-700 text-sm font-bold mb-2"
                                                >
                                                  Model:
                                                </label>
                                                <input
                                                  onChange={(e) => {
                                                    setModel(e.target.value);
                                                  }}
                                                  type="text"
                                                  id="Model"
                                                  class="border border-gray-400 rounded-md px-3 py-2 w-full"
                                                />
                                              </div>
                                              <div class="mb-2">
                                                <label
                                                  for="part-number"
                                                  class="block text-gray-700 text-sm font-bold mb-2"
                                                >
                                                  Part Number Customer :
                                                </label>
                                                <input
                                                  type="text"
                                                  onChange={(e) => {
                                                    setPnc(e.target.value);
                                                  }}
                                                  id="part-number"
                                                  class="border border-gray-400 rounded-md px-3 py-2 w-full"
                                                />
                                              </div>

                                              <div className="mb-2">
                                                <label
                                                  htmlFor="Dat"
                                                  className="block text-gray-700 text-sm font-bold mb-2"
                                                >
                                                  Date Target
                                                </label>
                                                <input
                                                  onChange={handleDateChange}
                                                  type="datetime-local"
                                                  class="border border-gray-400 rounded-md px-3 py-2 w-full"
                                                />
                                              </div>
                                              <div className=" flex ">
                                                <div class="mb-2">
                                                  <label
                                                    for="Week"
                                                    class="block text-gray-700 text-sm font-bold mb-2"
                                                  >
                                                    Week Target :
                                                  </label>
                                                  <input
                                                    onChange={(e) => {
                                                      setWeek(e.target.value);
                                                    }}
                                                    type="number"
                                                    id="Week"
                                                    class="border border-gray-400 rounded-md px-3 py-2 w-full"
                                                  />
                                                </div>
                                                <div class="mb-2 ml-16">
                                                  <label
                                                    for="Stock"
                                                    class="block text-gray-700 text-sm font-bold mb-2"
                                                  >
                                                    Stock:
                                                  </label>
                                                  <input
                                                    onChange={(e) => {
                                                      setStock(e.target.value);
                                                    }}
                                                    type="number"
                                                    id="Stock"
                                                    class="border border-gray-400 rounded-md px-3 py-2 w-full"
                                                  />
                                                </div>
                                              </div>
                                              <div className=" flex ">
                                                <div class="mb-2">
                                                  <label
                                                    for="Wip"
                                                    class="block text-gray-700 text-sm font-bold mb-2"
                                                  >
                                                    Wip:
                                                  </label>
                                                  <input
                                                    onChange={(e) => {
                                                      setWip(e.target.value);
                                                    }}
                                                    type="number"
                                                    id="Wip"
                                                    class="border border-gray-400 rounded-md px-3 py-2 w-full"
                                                  />
                                                </div>
                                                <div class="mb-2 ml-16">
                                                  <label
                                                    for="SE"
                                                    class="block text-gray-700 text-sm font-bold mb-2"
                                                  >
                                                    Stock End:
                                                  </label>
                                                  <input
                                                    type="text"
                                                    value={SE}
                                                    readOnly
                                                    id="SE"
                                                    class="border border-gray-400 bg-gray-200 rounded-md px-3 py-2 w-full appearance-none block"
                                                  />
                                                </div>
                                              </div>
                                            </form>
                                          </div>
                                          <div className="bg-gray-50 px-4 py-3 sm:flex sm:flex-row-reverse sm:px-6">
                                            <button
                                              onClick={SubmitPPIC}
                                              type="button"
                                              class="inline-flex w-full justify-center rounded-md bg-red-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-red-500 sm:ml-3 sm:w-auto"
                                            >
                                              Submit
                                            </button>
                                            <button
                                              type="button"
                                              onClick={() => {
                                                setIsModelW1(false);
                                                window.location.reload();
                                              }}
                                              class="mt-3 inline-flex w-full justify-center rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50 sm:mt-0 sm:w-auto"
                                            >
                                              Cancel
                                            </button>
                                          </div>
                                        </div>
                                      </div>
                                    </div>
                                  </div>
                                </>
                              ) : null}
                            </div>
                          </div>
                        </div>

                        <div className="block w-full overflow-x-auto overflow-y-scroll max-h-[330px] h-[330px] ">
                          <table
                            id="data-table"
                            className="items-center bg-transparent w-full border-collapse"
                          >
                            <thead className="bg-[#BFBE9C]">
                              <tr>
                                <th className="px-2 py-3 text-xs uppercase font-semibold text-left">
                                  Model
                                </th>
                                <th className="px-4 py-3 text-xs uppercase font-semibold text-left">
                                  Part Number Customer
                                </th>
                                <th className="px-6 py-3 text-xs uppercase font-semibold text-left">
                                  Date Target
                                </th>
                                <th className="px-6 py-3 text-xs uppercase font-semibold text-left">
                                  WEEK
                                </th>
