                                                      <button
                                                        type="button"
                                                        onClick={() => {
                                                          setSelectedItemWip(
                                                            false
                                                          );
                                                          window.location.reload();
                                                        }}
                                                        class="mt-3 inline-flex w-full justify-center rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50 sm:mt-0 sm:w-auto"
                                                      >
                                                        Cancel
                                                      </button>
                                                    </div>
                                                  </div>
                                                </div>
                                              </div>
                                            </div>
                                          </>
                                        )}
                                      </td>
                                    </>
                                  ) : null}
                                  {isDelet ? (
                                    <>
                                      <td class=" text-xs whitespace-nowrap border-t-0 font-medium ">
                                        <button
                                          onClick={() =>
                                            setSelectedItem(item.No)
                                          }
                                          className="trash-button"
                                        >
                                          <svg
                                            xmlns="http://www.w3.org/2000/svg"
                                            className="h-6 w-6"
                                            fill="none"
                                            viewBox="0 0 24 24"
                                            stroke="currentColor"
                                          >
                                            <path
                                              strokeLinecap="round"
                                              strokeLinejoin="round"
                                              strokeWidth={2}
                                              d="M6 18L18 6M6 6l12 12"
                                            />
                                          </svg>
                                        </button>

                                        {selectedItem && (
                                          <>
                                            <div
                                              class="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity"
                                              aria-labelledby="modal-title"
                                              role="dialog"
                                              aria-modal="true"
                                              style={{ zIndex: 50 }}
                                            >
                                              <div class="fixed inset-0 z-50 mt-32 overflow-y-auto">
                                                <div class="flex items-end justify-center text-center sm:items-center sm:p-0">
                                                  <div class="relative transform overflow-hidden rounded-lg bg-white text-left shadow-xl transition-all sm:my-8 sm:w-full sm:max-w-lg">
                                                    <div class="bg-white px-4 pb-4 pt-5 sm:p-6 sm:pb-4">
                                                      <div class="sm:flex sm:items-start">
                                                        <div class="mx-auto flex h-12 w-12 flex-shrink-0 items-center justify-center rounded-full bg-red-100 sm:mx-0 sm:h-10 sm:w-10">
                                                          <svg
                                                            class="h-6 w-6 text-red-600"
                                                            fill="none"
                                                            viewBox="0 0 24 24"
                                                            stroke-width="1.5"
                                                            stroke="currentColor"
                                                            aria-hidden="true"
                                                          >
                                                            <path
                                                              stroke-linecap="round"
                                                              stroke-linejoin="round"
                                                              d="M12 9v3.75m-9.303 3.376c-.866 1.5.217 3.374 1.948 3.374h14.71c1.73 0 2.813-1.874 1.948-3.374L13.949 3.378c-.866-1.5-3.032-1.5-3.898 0L2.697 16.126zM12 15.75h.007v.008H12v-.008z"
                                                            />
                                                          </svg>
                                                        </div>
                                                        <div class="mt-3 text-center sm:ml-4 sm:mt-0 sm:text-left">
                                                          <h3
                                                            class="text-base font-semibold leading-6 text-gray-900"
                                                            id="modal-title"
                                                          >
                                                            Delet Data
                                                          </h3>
                                                          <div class="mt-2">
                                                            <p class="text-sm text-gray-500">
                                                              Are you sure you
                                                              want to delet
                                                              data?
                                                            </p>
                                                          </div>
                                                        </div>
                                                      </div>
                                                    </div>
                                                    <div className="bg-gray-50 px-4 py-3 sm:flex sm:flex-row-reverse sm:px-6">
                                                      <button
                                                        onClick={handleDelete}
                                                        type="button"
                                                        class="inline-flex w-full justify-center rounded-md bg-red-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-red-500 sm:ml-3 sm:w-auto"
                                                      >
                                                        Submit
                                                      </button>
                                                      <button
                                                        onClick={() =>
                                                          setSelectedItem(false)
                                                        }
                                                        type="button"
                                                        // onClick={() =>
                                                        //   setSelectedItem(false)
                                                        // }
                                                        class="mt-3 inline-flex w-full justify-center rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50 sm:mt-0 sm:w-auto"
                                                      >
                                                        Cancel
                                                      </button>
                                                    </div>
                                                  </div>
                                                </div>
                                              </div>
                                            </div>
                                          </>
                                        )}
                                      </td>
                                    </>
                                  ) : null}

                                  <td></td>
                                </tr>
                              ))}
                            </tbody>
                          </table>
                        </div>
                      </div>
                    </div>
                    <footer class="relative  mt-16">
                      <div class="container mx-auto px-4">
                        <div class="flex flex-wrap items-center md:justify-between justify-center">
                          <div class="w-full md:w-6/12 px-4 mx-auto text-center"></div>
                        </div>
                      </div>
                    </footer>
                  </section>
                </div>
              </div>
            </div>
          </>
        ) : null}
      </main>
    </body>
  );
};

export default PPICProd;
