import React, { useState, useEffect } from "react";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";

const PPICProd = () => {
  const [isWeek1, setIsWeek1] = useState(true);
  const [isWeek2, setIsWeek2] = useState(false);
  const [isOpen1, setIsOpen1] = useState(false);
  const [Model, setModel] = useState("");
  const [Pnc, setPnc] = useState("");
  const [Week, setWeek] = useState("");
  const [Stock, setStock] = useState("");
  const [SE, setSE] = useState("");
  const [Wip, setWip] = useState("");
  const [QC, setQC] = useState("");
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);

  const [selectedItem, setSelectedItem] = useState(null);

  const [initialStock, setInitialStock] = useState(0);
  const [initialWip, setinitialWip] = useState(0);
  const [initialSE, setinitialSE] = useState(0);
  const [initialWeek, setInitialWeek] = useState(0);

  const exportToPDF = () => {
    const doc = new jsPDF();
    const table = document.getElementById("data-table");

    // Menambahkan tema "plain"

    doc.autoTable({ html: table, theme: "grid" });
    doc.save("PPIC.pdf");
  };

  useEffect(() => {
    fetch("http://10.14.81.43:3001/api/get/Week1")
      .then((response) => response.json())
      .then((json) => {
        // mengubah properti timestamp menjadi tanggal dan Date
        json.forEach((item) => {
          const date = new Date(item.Date);
          const day = date.getDate();
          const month = date.getMonth() + 1;
          const year = date.getFullYear();
          const hours = date.getHours();
          const minutes = date.getMinutes();
          const formattedDate = `${day.toString().padStart(2, "0")}-${month
            .toString()
            .padStart(2, "0")}-${year} / ${hours
            .toString()
            .padStart(2, "0")}:${minutes.toString().padStart(2, "0")}`;
          item.Date = formattedDate;
        });
        json.sort((a, b) => Date.parse(a.Date) - Date.parse(b.Date));
        json.reverse();
        setData(json);
        setFilteredData(json);
      });
  }, []);

  // Submit Network

  useEffect(() => {
    if (Stock !== "" && Week !== "") {
      const SEValue = parseInt(Stock) - parseInt(Week);
      setSE(SEValue.toString());
    } else {
      setSE("");
    }
  }, [Week, Stock]);

  //-----------------------------
  useEffect(() => {
    if (selectedItem) {
      setInitialStock(selectedItem.Stock);
      setStock(selectedItem.Stock);
      setInitialWeek(selectedItem.Week);
      setWeek(selectedItem.Week);
      setinitialWip(selectedItem.Wip);
      setWip(selectedItem.Wip);
    }
  }, [selectedItem]);

  useEffect(() => {
    if (QC === "") {
      // Mengatur nilai Stock kembali ke nilai awal
      setWip(initialWip);
      setStock(initialStock);
    } else {
      // Mengupdate nilai Stock dengan menambahkan nilai QC
      const newWip = initialWip - parseInt(QC);
      setWip(newWip);
      const newStock = initialStock + parseInt(QC);
      setStock(newStock);
    }
  }, [QC, initialStock, initialWip]);

  //

  useEffect(() => {
    if (Stock !== "" && Week !== "") {
      const newSE = parseInt(Stock) - parseInt(Week);
      setSE(newSE);
    } else {
      setSE(initialSE);
    }
  }, [Stock, Week, initialSE]);

  // ----------------------

  const UpdateQC = (event) => {
    const data = {
      Stock: parseInt(Stock),
      Wip: parseInt(Wip),
      SE: parseInt(SE),
      QC: parseInt(QC),
    };
    fetch(`http://10.14.81.43:3001/api/put/Week1QC/${selectedItem.No}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => {
        if (response.status === 200) {
          alert("Change Model Telah Selesai Data Sudah Terinput");
          window.location.reload();
          event.preventDefault();
        } else {
          throw new Error("Error updating data");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <body
      className="h-full w-full"
      style={{
        height: "200vh",
        background: "linear-gradient(90deg, #697B93 50%, #4A5568 50%)",
      }}
    >
      <button
        data-drawer-target="logo-sidebar"
        data-drawer-toggle="logo-sidebar"
        aria-controls="logo-sidebar"
        // onClick={toggleDrawer}
        type="button"
        class="inline-flex items-center p-2 mt-2 ml-3 text-sm text-gray-500 rounded-lg md:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200 dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600"
      >
        <span class="sr-only">Open sidebar</span>
        <svg
          class="w-6 h-6"
          aria-hidden="true"
          fill="currentColor"
          viewBox="0 0 20 20"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            clip-rule="evenodd"
            fill-rule="evenodd"
            d="M2 4.75A.75.75 0 012.75 4h14.5a.75.75 0 010 1.5H2.75A.75.75 0 012 4.75zm0 10.5a.75.75 0 01.75-.75h7.5a.75.75 0 010 1.5h-7.5a.75.75 0 01-.75-.75zM2 10a.75.75 0 01.75-.75h14.5a.75.75 0 010 1.5H2.75A.75.75 0 012 10z"
          ></path>
        </svg>
      </button>

      <aside
        id="logo-sidebar"
        class="fixed top-0 left-0 z-40 w-44 h-screen transition-transform -translate-x-full  sm:translate-x-0"
        aria-label="Sidebar"
      >
        <div class="h-full px-3 py-4 overflow-y-auto bg-gray-50 dark:bg-gray-800">
          <a href="/Pin" class="flex items-center pl-2.5 mb-5">
            <img
              src={process.env.PUBLIC_URL + "/AVI.png"}
              class="h-6 mr-3 sm:h-7"
              alt="Flowbite Logo"
            />
          </a>
          <ul class="space-y-2">
            <li>
              <button
                onClick={() => {
                  setIsOpen1(false);
                  setIsWeek1(true);
                  setIsWeek2(false);
                }}
                class="flex items-center p-2 text-base font-normal text-gray-900 rounded-lg dark:text-white hover:bg-gray-100 dark:hover:bg-gray-700"
              >
                <svg
                  aria-hidden="true"
                  class="flex-shrink-0 w-6 h-6 text-gray-500 transition duration-75 dark:text-gray-400 group-hover:text-gray-900 dark:group-hover:text-white"
                  fill="currentColor"
                  viewBox="0 0 20 20"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path d="M5 3a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2V5a2 2 0 00-2-2H5zM5 11a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2v-2a2 2 0 00-2-2H5zM11 5a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V5zM11 13a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z"></path>
                </svg>
                <span class="flex-1 ml-3 whitespace-nowrap">Week 1</span>
              </button>
            </li>
            <li>
              <button
                onClick={() => {
                  setIsOpen1(false);
                  setIsWeek1(false);
                  setIsWeek2(true);
                }}
                class="flex items-center p-2 text-base font-normal text-gray-900 rounded-lg dark:text-white hover:bg-gray-100 dark:hover:bg-gray-700"
              >
                <svg
                  aria-hidden="true"
                  class="flex-shrink-0 w-6 h-6 text-gray-500 transition duration-75 dark:text-gray-400 group-hover:text-gray-900 dark:group-hover:text-white"
                  fill="currentColor"
                  viewBox="0 0 20 20"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path d="M5 3a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2V5a2 2 0 00-2-2H5zM5 11a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2v-2a2 2 0 00-2-2H5zM11 5a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V5zM11 13a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z"></path>
                </svg>
                <span class="flex-1 ml-3 whitespace-nowrap">Week 2</span>
              </button>
            </li>
            <li>
              <button class="flex items-center p-2 text-base font-normal text-gray-900 rounded-lg dark:text-white hover:bg-gray-100 dark:hover:bg-gray-700">
                <svg
                  aria-hidden="true"
                  class="flex-shrink-0 w-6 h-6 text-gray-500 transition duration-75 dark:text-gray-400 group-hover:text-gray-900 dark:group-hover:text-white"
                  fill="currentColor"
                  viewBox="0 0 20 20"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path d="M5 3a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2V5a2 2 0 00-2-2H5zM5 11a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2v-2a2 2 0 00-2-2H5zM11 5a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V5zM11 13a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z"></path>
                </svg>
                <span class="flex-1 ml-3 whitespace-nowrap">Week 3</span>
              </button>
            </li>
            <li>
              <button class="flex items-center p-2 text-base font-normal text-gray-900 rounded-lg dark:text-white hover:bg-gray-100 dark:hover:bg-gray-700">
                <svg
                  aria-hidden="true"
                  class="flex-shrink-0 w-6 h-6 text-gray-500 transition duration-75 dark:text-gray-400 group-hover:text-gray-900 dark:group-hover:text-white"
                  fill="currentColor"
                  viewBox="0 0 20 20"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path d="M5 3a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2V5a2 2 0 00-2-2H5zM5 11a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2v-2a2 2 0 00-2-2H5zM11 5a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V5zM11 13a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z"></path>
                </svg>
                <span class="flex-1 ml-3 whitespace-nowrap">Week 4</span>
              </button>
            </li>
          </ul>
        </div>
      </aside>

      {/*  */}
      <main>
        {isWeek1 ? (
          <>
            <div className="fixed z-10 inset-0 overflow-y-auto">
              <div className="flex items-end justify-center min-h-screen w-[880px] pt-4 px-4 ml-48 md:ml-48 sm:ml-56 lg:ml-80 pb-0 text-center sm:block sm:p-0">
                <div className="fixed inset-0 transition-opacity">
                  <div className="absolute bg-gray-500 opacity-75"></div>
                </div>
                <span className="hidden sm:inline-block sm:align-middle sm:h-screen"></span>
                <div
                  className="inline-block  align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-5xl sm:w-full"
                  role="dialog"
                  aria-modal="true"
                  aria-labelledby="modal-headline"
                >
                  <section class="py-1 bg-[#2A4C59]">
                    <div className="w-full xl:w-8/12 mb-12 xl:mb-0 px-4 justify-center ml-2 mt-10">
                      <div className="relative flex flex-col min-w-0 break-words bg-white w-[830px] h-96  mb-6 shadow-lg rounded">
                        <div className="rounded-t mb-0 px-4 py-3 border-0">
                          <div className="flex flex-wrap items-center overflow-auto">
                            <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                              <h3 className="font-semibold text-base text-blueGray-700">
                                Week 1 QC
                              </h3>
                            </div>
                            <button
                              className="bg-red-500 text-white active:bg-red-500  text-xs font-bold uppercase px-3 py-1 rounded outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                              type="button"
                              onClick={exportToPDF}
                            >
                              PDF
                            </button>
                          </div>
                        </div>

                        <div className="block w-full overflow-x-auto overflow-y-scroll max-h-[330px] h-[330px]">
                          <table
                            id="data-table"
                            className="items-center bg-transparent w-full border-collapse"
                          >
                            <thead className="bg-[#23606E]">
                              <tr>
                                <th className="px-2 py-3 text-xs uppercase font-semibold text-left">
                                  Model
                                </th>
                                <th className="px-4 py-3 text-xs uppercase font-semibold text-left">
                                  Part Number Customer
                                </th>
                                <th className="px-5  py-3 text-xs uppercase font-semibold text-left">
                                  Date Target
                                </th>
                                <th className="px-6 py-3 text-xs uppercase font-semibold text-left">
                                  WEEK
                                </th>
                                <th className="px-6 py-3 text-xs uppercase font-semibold text-left">
                                  Stock
                                </th>
                                <th className="px-6 py-3 text-xs uppercase font-semibold text-left">
                                  WIP
                                </th>
                                <th className="px-4 py-3 text-xs uppercase font-semibold text-left">
                                  Stok End
                                </th>
                                <th className="px-6 py-3 text-xs uppercase font-semibold text-left">
                                  QC
                                </th>
                                <th className=" text-xs uppercase font-semibold text-left"></th>
                              </tr>
                            </thead>

                            <tbody>
                              {filteredData.map((item, inex) => (
                                <tr className="bg-[#587E8C]" key={item.No}>
                                  <td class="px-2 py-4 text-xs font-medium  whitespace-nowrap border-t-0">
                                    {item.Model}
                                  </td>
                                  <td class="px-4 py-4 text-xs font-medium  whitespace-nowrap border-t-0">
                                    {item.Pnc}
                                  </td>
                                  <td class="px-1 py-1 text-xs whitespace-nowrap border-t-0 font-medium ">
                                    <i class="fas fa-arrow-up text-emerald-500 mr-4"></i>
                                    {item.Date}
                                  </td>
                                  <td class="px-3 py-4 text-xs font-medium text-red-800 whitespace-nowrap border-t-0">
                                    <i class="fas fa-arrow-up text-emerald-500 mr-4"></i>
                                    {item.Week}
                                  </td>
                                  <td class="px-3 py-4 text-xs whitespace-nowrap text-orange-500 border-t-0 font-medium ">
                                    <i class="fas fa-arrow-up text-emerald-500 mr-4"></i>
                                    {item.Stock}
                                  </td>
                                  <td class="px-2 py-4 text-xs whitespace-nowrap border-t-0 font-medium ">
                                    <i class="fas fa-arrow-up text-emerald-500 mr-4"></i>
                                    {item.Wip}
                                  </td>
                                  <td class="px-1 py-4 text-xs whitespace-nowrap text-amber-200  border-t-0 font-medium ">
                                    <i class="fas fa-arrow-up text-emerald-500 mr-4"></i>
                                    {item.Stokend}
                                  </td>
                                  <td class="px-3 py-4 text-xs whitespace-nowrap border-t-0 font-medium ">
                                    <i class="fas fa-arrow-up text-emerald-500 mr-4"></i>
                                    {item.Qc}
                                  </td>
                                  <td class=" text-xs whitespace-nowrap border-t-0 font-medium ">
                                    <button
                                      className="bg-green-500 text-white active:bg-green-600 text-xs font-bold uppercase px-3 py-1 rounded outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                                      type="button"
                                      onClick={() => setSelectedItem(item)}
                                    >
                                      Add
                                    </button>
                                    {selectedItem && (
                                      <>
                                        <div
                                          class="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity"
                                          aria-labelledby="modal-title"
                                          role="dialog"
                                          aria-modal="true"
                                          style={{ zIndex: 50 }}
                                        >
                                          <div class="fixed inset-0 z-50 P overflow-y-auto">
                                            <div class="flex items-end justify-center text-center sm:items-center sm:p-0">
                                              <div class="relative transform overflow-hidden rounded-lg bg-white text-left shadow-xl transition-all sm:my-8 sm:w-full sm:max-w-lg">
                                                <div class="bg-white px-4 pb-4 pt-5 sm:p-6 sm:pb-4">
                                                  <div className="sm:flex sm:items-start">
                                                    <div className="mx-auto flex h-12 w-12 flex-shrink-0 items-center justify-center rounded-full bg-slate-300  sm:mx-0 sm:h-10 sm:w-10">
                                                      <svg
                                                        width="800px"
                                                        height="800px"
                                                        viewBox="0 0 1024 1024"
                                                        class="icon"
                                                        version="1.1"
                                                        xmlns="http://www.w3.org/2000/svg"
                                                      >
                                                        <path
                                                          d="M515.1 920.6H228V104.2h567.7v339.6c0 11 9 20 20 20s20-9 20-20V96c0-17.5-14.3-31.8-31.8-31.8H219.7C202.2 64.2 188 78.4 188 96v832.9c0 17.5 14.3 31.8 31.8 31.8h295.4c11 0 20-9 20-20s-9-20.1-20.1-20.1z"
                                                          fill="#666666"
                                                        />
                                                        <path
                                                          d="M749 384.6c0-11-9-20-20-20H283c-11 0-20 9-20 20s9 20 20 20h446c11 0 20-9 20-20zM541 522.1c0-11-6.9-20-15.2-20H278.3c-8.4 0-15.2 9-15.2 20s6.9 20 15.2 20h247.4c8.4 0 15.3-9 15.3-20zM278.3 640.6c-8.4 0-15.2 9-15.2 20s6.9 20 15.2 20h168.6c8.4 0 15.2-9 15.2-20s-6.9-20-15.2-20H278.3zM736.8 509.5c-124.3 0-225 100.7-225 225s100.7 225 225 225 225-100.7 225-225-100.8-225-225-225zM900.2 663l-188 188c-0.7 1.4-1.6 2.6-2.8 3.8-2.3 2.3-5.2 3.7-8.3 4.2-5.1 1.3-10.6 0-14.6-3.9-1.4-1.4-2.5-3.1-3.2-4.8l-103.8-104c-5.9-5.9-5.9-15.6 0-21.5s15.6-5.9 21.5 0l97.1 97.1 180.5-180.5c5.9-5.9 15.6-5.9 21.5 0 6 6 6 15.7 0.1 21.6z"
                                                          fill="#666666"
                                                        />
                                                        <path
                                                          d="M726.8 300.1h-429c-14.8 0-26.8-12-26.8-26.8v-81.4c0-14.8 12-26.8 26.8-26.8h429c14.8 0 26.8 12 26.8 26.8v81.4c0 14.8-12 26.8-26.8 26.8z"
                                                          fill="#CCCCCC"
                                                        />
                                                      </svg>
                                                    </div>
                                                    <span className="ml-auto mt-2 font-bold">
                                                      Date Target: <br />{" "}
                                                      {selectedItem.Date}
                                                    </span>
                                                  </div>
                                                  <form>
                                                    <div class="mb-2">
                                                      <label
                                                        for="Model"
                                                        class="block text-gray-700 bg text-sm font-bold mb-2"
                                                      >
                                                        Model:
                                                      </label>
                                                      <input
                                                        type="text"
                                                        id="Model"
                                                        class="border border-gray-400 bg-gray-200 rounded-md px-3 py-3 w-full appearance-none block"
                                                        value={
                                                          selectedItem.Model
                                                        }
                                                      />
                                                    </div>
                                                    <div class="mb-2">
                                                      <label
                                                        for="part-number"
                                                        class="block text-gray-700 text-sm font-bold mb-2"
                                                      >
                                                        Part Number Customer :
                                                      </label>
                                                      <input
                                                        type="text"
                                                        id="part-number"
                                                        class="border border-gray-400 bg-gray-200 rounded-md px-3 py-3 w-full appearance-none block"
                                                        value={selectedItem.Pnc}
                                                      />
                                                    </div>
                                                    <div className="flex justify-center items-center">
                                                      <div class="mb-2">
                                                        <label
                                                          for="Week"
                                                          class="block text-gray-700  text-sm font-bold mb-2"
                                                        >
                                                          Week Target :
                                                        </label>
                                                        <input
                                                          type="text"
                                                          id="Week"
                                                          class="border border-gray-400 bg-gray-200 rounded-md px-3 py-3 w-full appearance-none block"
                                                          value={
                                                            selectedItem.Week
                                                          }
                                                        />
                                                      </div>
                                                      <div class="mb-2 ml-16">
                                                        <label
                                                          for="Stock"
                                                          class="block text-gray-700 text-sm font-bold mb-2"
                                                        >
                                                          Stock:
                                                        </label>
                                                        <input
                                                          type="text"
                                                          id="Stock"
                                                          readOnly
                                                          class="border border-gray-400 bg-gray-200 rounded-md px-3 py-3 w-full appearance-none block"
                                                          value={
                                                            Stock ||
                                                            initialStock
                                                          }
                                                        />
                                                      </div>
                                                    </div>
                                                    <div className="flex justify-center items-center">
                                                      <div class="mb-2">
                                                        <label
                                                          for="Wip"
                                                          class="block text-gray-700 text-sm font-bold mb-2"
                                                        >
                                                          Wip:
                                                        </label>
                                                        <input
                                                          type="text"
                                                          id="Wip"
                                                          class="border border-gray-400 bg-gray-200 rounded-md px-3 py-3 w-full appearance-none block"
                                                          value={Wip}
                                                        />
                                                      </div>
                                                      <div class="mb-2 ml-16">
                                                        <label
                                                          for="SE"
                                                          class="block text-gray-700 text-sm font-bold mb-2"
                                                        >
                                                          Stock End:
                                                        </label>
                                                        <input
                                                          type="text"
                                                          id="SE"
                                                          class="border border-gray-400 bg-gray-200 rounded-md px-3 py-3 w-full appearance-none block"
                                                          value={SE}
                                                        />
                                                      </div>
                                                    </div>
                                                    <div>
                                                      <label
                                                        for="SE"
                                                        class="block text-gray-700 text-sm font-bold mb-2"
                                                      >
                                                        QC:
                                                      </label>
                                                      <input
                                                        type="Number"
                                                        id="SE"
                                                        class="border border-gray-400 rounded-md px-3 py-2 w-full"
                                                        onChange={(e) => {
                                                          setQC(e.target.value);
                                                        }}
                                                      />
                                                    </div>
                                                  </form>
                                                </div>
                                                <div className="bg-gray-50 px-4 py-3 sm:flex sm:flex-row-reverse sm:px-6">
                                                  <button
                                                    onClick={UpdateQC}
                                                    type="button"
                                                    class="inline-flex w-full justify-center rounded-md bg-red-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-red-500 sm:ml-3 sm:w-auto"
                                                  >
                                                    Submit
                                                  </button>
                                                  <button
                                                    type="button"
                                                    onClick={() =>
                                                      setSelectedItem(false)
                                                    }
                                                    class="mt-3 inline-flex w-full justify-center rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50 sm:mt-0 sm:w-auto"
                                                  >
                                                    Cancel
                                                  </button>
                                                </div>
                                              </div>
                                            </div>
                                          </div>
                                        </div>
                                      </>
                                    )}
                                  </td>
                                </tr>
                              ))}
                            </tbody>
                          </table>
                        </div>
                      </div>
                    </div>
                    <footer class="relative  mt-16">
                      <div class="container mx-auto px-4">
                        <div class="flex flex-wrap items-center md:justify-between justify-center">
                          <div class="w-full md:w-6/12 px-4 mx-auto text-center"></div>
                        </div>
                      </div>
                    </footer>
                  </section>
                </div>
              </div>
            </div>
          </>
        ) : null}
      </main>
    </body>
  );
};

export default PPICProd;
