import React, { useState, useEffect } from "react";
import firebase from "firebase/compat/app";
import "firebase/compat/database";

const firebaseConfig = {
  apiKey: "AIzaSyBF8Q3AuT5m-iBayXDt82r8RyADUdnyGsk",
  databaseURL:
    "https://classroom-1a392-default-rtdb.asia-southeast1.firebasedatabase.app",
};
firebase.initializeApp(firebaseConfig);

const database = firebase.database();

const ClassA = () => {
  const [chrisna, setchrisna] = useState("");
  const [backgroundColorChrisna, setBackgroundColorChrisna] = useState("");
  const [Charlotte, setCharlotte] = useState("");
  const [backgroundColorCharlotte, setBackgroundColorCharlotte] = useState("");
  const [Anggi, setAnggi] = useState("");
  const [backgroundColorAnggi, setBackgroundColorAnggi] = useState("");
  const [Sophia, setSophia] = useState("");
  const [backgroundColorSophia, setBackgroundColorSophia] = useState("");
  const [Emily, setEmily] = useState("");
  const [backgroundColorEmily, setBackgroundColorEmily] = useState("");
  const [Violet, setViolet] = useState("");
  const [backgroundColorViolet, setBackgroundColorViolet] = useState("");
  const [William, setWilliam] = useState("");
  const [backgroundColorWilliam, setBackgroundColorWilliam] = useState("");
  const [Lucas, setLucas] = useState("");
  const [backgroundColorLucas, setBackgroundColorLucas] = useState("");
  const [Henry, setHenry] = useState("");
  const [backgroundColorHenry, setBackgroundColorHenry] = useState("");
  const [selectedItem, setSelectedItem] = useState(null);
  const [data, setData] = useState([]);

  const [OpenChrisna, setOpenChrisna] = useState(null);
  const [OpenCharlotte, setOpenCharlotte] = useState(null);

  const [EditProfil, setEditProfil] = useState(false);

  // SUBMIT FILE
  const [file, setFile] = useState(null);

  // Update
  const [No, setNo] = useState("");
  const [Name, setName] = useState("");
  const [Addres, setAddres] = useState("");
  const [Contact, setContact] = useState("");
  const [BornDate, setBornDate] = useState("");

  useEffect(() => {
    const ref1 = firebase.database().ref("ClassA/Chrisna");
    ref1.on("value", (snapshot) => {
      const data = snapshot.val();
      updateChrisna(data);
    });

    const ref2 = firebase.database().ref("ClassA/Charlotte");
    ref2.on("value", (snapshot) => {
      const data = snapshot.val();
      updateCharlotte(data);
    });

    const ref3 = firebase.database().ref("ClassA/Anggi");
    ref3.on("value", (snapshot) => {
      const data = snapshot.val();
      updateAnggi(data);
    });

    const ref4 = firebase.database().ref("ClassA/Sophia");
    ref4.on("value", (snapshot) => {
      const data = snapshot.val();
      updateSophia(data);
    });

    const ref5 = firebase.database().ref("ClassA/Emily");
    ref5.on("value", (snapshot) => {
      const data = snapshot.val();
      updateEmily(data);
    });

    const ref6 = firebase.database().ref("ClassA/Violet");
    ref6.on("value", (snapshot) => {
      const data = snapshot.val();
      updateViolet(data);
    });

    const ref7 = firebase.database().ref("ClassA/William");
    ref7.on("value", (snapshot) => {
      const data = snapshot.val();
      updateWilliam(data);
    });

    const ref8 = firebase.database().ref("ClassA/Lucas");
    ref8.on("value", (snapshot) => {
      const data = snapshot.val();
      updateLucas(data);
    });

    const ref9 = firebase.database().ref("ClassA/Henry");
    ref9.on("value", (snapshot) => {
      const data = snapshot.val();
      updateHenry(data);
    });
    return () => {};
  }, []);

  const updateChrisna = (data) => {
    setchrisna(data);
    setBackgroundColorChrisna(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateCharlotte = (data) => {
    setCharlotte(data);
    setBackgroundColorCharlotte(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateAnggi = (data) => {
    setAnggi(data);
    setBackgroundColorAnggi(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateSophia = (data) => {
    setSophia(data);
    setBackgroundColorSophia(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateViolet = (data) => {
    setViolet(data);
    setBackgroundColorViolet(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateEmily = (data) => {
    setEmily(data);
    setBackgroundColorEmily(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateWilliam = (data) => {
    setWilliam(data);
    setBackgroundColorWilliam(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateLucas = (data) => {
    setLucas(data);
    setBackgroundColorLucas(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  const updateHenry = (data) => {
    setHenry(data);
    setBackgroundColorHenry(
      data === "Present"
        ? "#84BF56"
        : data === "Not present"
        ? "#C00000"
        : "#565454"
    );
  };

  // Fungsi untuk menghitung jumlah nilai "Present"
  const countPresent = () => {
    const variables = [
      chrisna,
      Charlotte,
      Anggi,
      Sophia,
      Emily,
      Violet,
      William,
      Lucas,
      Henry,
    ];
    let presentCount = 0;
    for (let i = 0; i < variables.length; i++) {
      if (variables[i] === "Present") {
        presentCount++;
      }
    }
    return presentCount;
  };

  useEffect(() => {
    fetch("http://localhost:3001/api/get/students")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
      })
      .catch((error) => {
        // Tangani error jika permintaan gagal
        console.error("Error fetching data:", error);
      });
  }, []);

  const updateProfile = () => {
    const data = new FormData();
    data.append("Name", Name);
    data.append("Addres", Addres);
    data.append("Contact", Contact);
    data.append("BornDate", BornDate);
    data.append("Picture", file);

    fetch(`http://192.168.101.236:3001/api/put/students/${No}`, {
      method: "PUT",
      body: data,
    })
      .then((response) => {
        if (response.status === 200) {
          alert("Data has been updated successfully");
          window.location.reload();
        } else {
          throw new Error("Error updating data");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // Handle file change
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  return (
    <main className=" ">

        <div
          style={{
            height: "200vh",
            background: "linear-gradient(90deg, #005A5B 50%, #007369 50%)",
          }}
        >
          <div className="items-center mx-auto text-center justify-center pt-No0 mb-10">
            <span className="font-semibold text-white text-2xl ">Class A</span>
          </div>
          
         
          <div class="flex ">
            <button
              className="mx-auto p-5 hover:scale-90"
              onClick={() => {
                setNo(1);
                setOpenChrisna(true);
              }}
            >
              <svg
                width="100px"
                viewBox="0 0 1024 1024"
                class="icon"
                version="1.1"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M768 544V64a64 64 0 0 0-64-64H320a64 64 0 0 0-64 64v480h512z"
                  fill={backgroundColorChrisna}
                />
                <path d="M256 512h512v32H256z" fill="" />
                <path
                  d="M608 160m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M512 288m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M416 160m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M608 416m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M416 416m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M256 992a32 32 0 1 0 64 0V672H256v320zM704 672v320a32 32 0 1 0 64 0V672h-64z"
                  fill="#434854"
                />
                <path
                  d="M832 640a32 32 0 0 1-32 32H224a32 32 0 0 1-32-32v-64a32 32 0 0 1 32-32h576a32 32 0 0 1 32 32v64z"
                  fill={backgroundColorChrisna}
                />
                <path
                  d="M800 544H224a32 32 0 0 0-32 32v32a32 32 0 0 1 32-32h576a32 32 0 0 1 32 32v-32a32 32 0 0 0-32-32z"
                  fill="#FFFFFF"
                />
                <path d="M704 672h64v32h-64zM256 672h64v32H256z" fill="" />
              </svg>
              <span key={index} class="font-thin text-white">
                ya
              </span>
            </button>

            <button
              onClick={() => {
                setNo(2);
                setOpenCharlotte(true);
              }}
              class="mx-auto p-5 hover:scale-90"
            >
              <svg
                width="100px"
                viewBox="0 0 1024 1024"
                class="icon"
                version="1.1"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M768 544V64a64 64 0 0 0-64-64H320a64 64 0 0 0-64 64v480h512z"
                  fill={backgroundColorCharlotte}
                />
                <path d="M256 512h512v32H256z" fill="" />
                <path
                  d="M608 160m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M512 288m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M416 160m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M608 416m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M416 416m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M256 992a32 32 0 1 0 64 0V672H256v320zM704 672v320a32 32 0 1 0 64 0V672h-64z"
                  fill="#434854"
                />
                <path
                  d="M832 640a32 32 0 0 1-32 32H224a32 32 0 0 1-32-32v-64a32 32 0 0 1 32-32h576a32 32 0 0 1 32 32v64z"
                  fill={backgroundColorCharlotte}
                />
                <path
                  d="M800 544H224a32 32 0 0 0-32 32v32a32 32 0 0 1 32-32h576a32 32 0 0 1 32 32v-32a32 32 0 0 0-32-32z"
                  fill="#FFFFFF"
                />
                <path d="M704 672h64v32h-64zM256 672h64v32H256z" fill="" />
              </svg>
              <span class="font-thin text-white">
                yaa
              </span>
            </button>

            <button class="mx-auto p-5 hover:scale-90">
              <svg
                width="100px"
                viewBox="0 0 1024 1024"
                class="icon"
                version="1.1"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M768 544V64a64 64 0 0 0-64-64H320a64 64 0 0 0-64 64v480h512z"
                  fill={backgroundColorAnggi}
                />
                <path d="M256 512h512v32H256z" fill="" />
                <path
                  d="M608 160m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M512 288m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M416 160m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M608 416m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M416 416m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
                  fill=""
                />
                <path
                  d="M256 992a32 32 0 1 0 64 0V672H256v320zM704 672v320a32 32 0 1 0 64 0V672h-64z"
                  fill="#434854"
                />
                <path
                  d="M832 640a32 32 0 0 1-32 32H224a32 32 0 0 1-32-32v-64a32 32 0 0 1 32-32h576a32 32 0 0 1 32 32v64z"
                  fill={backgroundColorAnggi}
                />
                <path
                  d="M800 544H224a32 32 0 0 0-32 32v32a32 32 0 0 1 32-32h576a32 32 0 0 1 32 32v-32a32 32 0 0 0-32-32z"
                  fill="#FFFFFF"
                />
                <path d="M704 672h64v32h-64zM256 672h64v32H256z" fill="" />
              </svg>
              <span class="font-thin text-white">Anggi</span>
            </button>
          </div>

          <div class="flex">
            <button class="mx-auto p-5 hover:scale-90">
              <svg
                width="100px"
                viewBox="0 0 1024 1024"
                class="icon"
                version="1.1"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M768 544V64a64 64 0 0 0-64-64H320a64 64 0 0 0-64 64v480h512z"
                  fill={backgroundColorSophia}
                />
                <path d="M256 512h512v32H256z" fill="" />
                <path
                  d="M608 160m-32 0a32 32 0 1 0 64 0 32 32 0 1 0-64 0Z"
