import React, { useState, useEffect } from "react";



const Teacher = () => {
  const [Name, setName] = useState("");
  const [Subject, setSubject] = useState("");
  const [Timein, setTimein] = useState("");
  const [Timeout, setTimeout] = useState("");
  const [data, setData] = useState([]);
  const [OpenChrisna, setOpenChrisna] = useState(true);





  // Submit PPIC / ADD
  const Add = (event) => {
    event.preventDefault();
    if (!Name || !Subject || !Timein || !Timeout) {
      alert("Harap isi semua kolom!");
      return;
    }
    const data = {
      Name: Name,
      Subject: Subject,
      Timein: Timein,
      Timeout: Timeout,
    };

    fetch(`http://localhost:3001/api/post/schedule`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => {
        if (response.status === 200) {
          alert("Schedule Succesfully Updated");
          window.location.reload();
        } else {
          throw new Error("Error adding data");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };
  

  useEffect(() => {
    fetch("http://localhost:3001/api/get/schedule")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
      })
      .catch((error) => {
        // Tangani error jika permintaan gagal
        console.error("Error fetching data:", error);
      });
  }, []);

  const Reset = () => {
    fetch("http://192.168.101.236:3001/api/delete/schedule", {
      method: "DELETE",
    })
      .then((response) => response.json())
      .then((data) => {
        alert("Schedule Succesfully Deleted");
        window.location.reload();
        console.log(data);
        setData([]);
      })
      .catch((error) => {
        console.error("Error resetting data:", error);
      });
  };

  return (
    <main>
      <div
        class="w-full  shadow-xl rounded-lg flex overflow-x-auto custom-scrollbar"
        style={{
          height: "200vh",
          background: "linear-gradient(90deg, #005A5B 50%, #007369 50%)",
        }}
      >
        <div class="w-64 px-4 ">
          <div class="h-16 flex items-center">
            <a
              href="/"
              class="w-48 mx-auto bg-blue-500 hover:bg-blue-700 flex items-center justify-center text-gray-100 py-2 rounded space-x-2 transition duration-150"
            >
              <span>Home</span>
            </a>
          </div>
          <div class="px-2 pt-4 pb-8 h-[600px]  border-r border-gray-300">
            <ul class="space-y-2">
              <li>
                <a class=" bg-opacity-30 text-blue-500 flex items-center justify-between py-1.5 px-4 rounded cursor-pointer">
                  <span class="flex items-center space-x-2">
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      class="h-5 w-5"
                      fill="none"
                      viewBox="0 0 24 24"
                      stroke="currentColor"
                    >
                      <path
                        stroke-linecap="round"
                        stroke-linejoin="round"
                        stroke-width="2"
                        d="M20 13V6a2 2 0 00-2-2H6a2 2 0 00-2 2v7m16 0v5a2 2 0 01-2 2H6a2 2 0 01-2-2v-5m16 0h-2.586a1 1 0 00-.707.293l-2.414 2.414a1 1 0 01-.707.293h-3.172a1 1 0 01-.707-.293l-2.414-2.414A1 1 0 006.586 13H4"
                      ></path>
                    </svg>
                    <span>Schedule</span>
                  </span>
                </a>
              </li>
            
              <li>
                <a class="hover: hover:bg-opacity-10 hover:text-blue-500 flex items-center text-white py-1.5 px-4 rounded space-x-2 cursor-pointer">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    class="h-5 w-5"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                  >
                    <path
                      stroke-linecap="round"
                      stroke-linejoin="round"
                      stroke-width="2"
                      d="M5 5a2 2 0 012-2h10a2 2 0 012 2v16l-7-3.5L5 21V5z"
                    ></path>
                  </svg>
                  <span>Information</span>
                </a>
              </li>
          
            </ul>
          </div>
        </div>

        <div class="flex-1 px-2">
          <div className="mt-12">
            <span className="text-white text-2xl font-semibold">
              Add Information Letter
            </span>
          </div>
          <div class="pt-4 ">
            <form class="w-full max-w-lg">
              <div class="flex  -mx-3 ">
                <div class="w-full px-3 mb-6 md:mb-0">
                  <label
                    class="block uppercase tracking-wide w-56 text-white text-xs font-bold mb-2"
                    for="grid-first-name"
                  >
                   No letter
                  </label>
                  <input
                    onChange={(e) => {
                      setName(e.target.value);
                    }}
                    class="appearance-none block w-full  bg-gray-200 text-gray-700 border  rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
                    id="grid-first-name"
                    type="text"
                    placeholder="Jane"
                  />
                </div>
                <div class="w-full px-3 mb-6 md:mb-0">
                  <label
                    class="block uppercase tracking-wide w-56 text-white text-xs font-bold mb-2"
                    for="grid-first-name"
                  >
                  Name Letter
                  </label>
                  <input
                    onChange={(e) => {
                      setName(e.target.value);
                    }}
                    class="appearance-none block w-full  bg-gray-200 text-gray-700 border  rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
                    id="grid-first-name"
                    type="text"
                    placeholder="Jane"
                  />
                </div>
               
                
              </div>
              <div class="w-full  mb-6 md:mb-0">
                  <label
                    class="block uppercase tracking-wide  text-white text-xs font-bold mb-2"
                    for="grid-first-name"
                  >
                   File
                  </label>
                  <input
                    onChange={(e) => {
                      setTimein(e.target.value);
                    }}
                    class="appearance-none block   bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
                    id="grid-first-name"
                    type="File"
                    placeholder="Jane"
                  />
                </div>
              
            </form>


            <div class="flex items-center justify-between mt-4">
                <div class="flex items-center space-x-2">
                <button
                    onClick={() => setOpenChrisna(true)}
                    class="bg-red-700  hover:bg-blue-700 rounded-lg px-12 py-1.5 text-gray-100 hover:shadow-xl transition duration-150"
                  >
                    Reset
                  </button>
                  <button
                    onClick={Add}
                    class="bg-blue-500 hover:bg-blue-700 rounded-lg px-12 py-1.5 text-gray-100 hover:shadow-xl transition duration-150"
                  >
                    Send
                  </button>
                </div>
              </div>

            <div className="flex  mt-6 border-spacing-1.5 flex-col">
              <div className="p-2 ml-72 mb-5">
                <span className="font-bold text-3xl p-2  text-white">
                  Todays Schedule
                </span>
              </div>
              <table class="table-auto border w-[900px]">
                <thead className="bg-black">
                  <tr className=" border">
                    <th class="px-4 text-white py-2 text-left">Teacher</th>
                    <th class="px-4 text-white py-2 text-left">Subject</th>
                    <th class="px-4 text-white py-2 text-left">Time</th>
                  </tr>
                </thead>
                <tbody className="bg-slate-700">
                  {data.map((item, index) => (
                    <tr key={index}>
                      <td class="px-4 font-medium text-white py-2">{item.Name}</td>
                      <td class="px-4 font-serif text-white py-2">{item.Subject}</td>
                      <td class="px-4 text-white py-2">
                       <span className="text-lime-400"> {item.Timein}</span> - <span className="text-red-400">{item.Timeout}</span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
      {OpenChrisna ? (
        <>
      <div id="staticModal" data-modal-backdrop="static" tabindex="-1" aria-hidden="true" class="fixed top-0 left-0 right-0 z-50 hidden w-full p-4 overflow-x-hidden overflow-y-auto md:inset-0 h-[calc(100%-1rem)] max-h-full">
    <div class="relative w-full max-w-2xl max-h-full">
       
        <div class="relative bg-white rounded-lg shadow dark:bg-gray-700">
           
            <div class="flex items-start justify-between p-4 border-b rounded-t dark:border-gray-600">
                <h3 class="text-xl font-semibold text-gray-900 dark:text-white">
                    Static modal
                </h3>
                <button type="button" class="text-gray-400 bg-transparent hover:bg-gray-200 hover:text-gray-900 rounded-lg text-sm w-8 h-8 ml-auto inline-flex justify-center items-center dark:hover:bg-gray-600 dark:hover:text-white" data-modal-hide="staticModal">
                    <svg class="w-3 h-3" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 14">
                        <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="m1 1 6 6m0 0 6 6M7 7l6-6M7 7l-6 6"/>
                    </svg>
                    <span class="sr-only">Close modal</span>
                </button>
            </div>
       
            <div class="p-6 space-y-6">
                <p class="text-base leading-relaxed text-gray-500 dark:text-gray-400">
                    With less than a month to go before the European Union enacts new consumer privacy laws for its citizens, companies around the world are updating their terms of service agreements to comply.
                </p>
                <p class="text-base leading-relaxed text-gray-500 dark:text-gray-400">
                    The European Union’s General Data Protection Regulation (G.D.P.R.) goes into effect on May 25 and is meant to ensure a common set of data rights in the European Union. It requires organizations to notify users as soon as possible of high-risk data breaches that could personally affect them.
                </p>
            </div>
          
            <div class="flex items-center p-6 space-x-2 border-t border-gray-200 rounded-b dark:border-gray-600">
                <button data-modal-hide="staticModal" type="button" class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">I accept</button>
                <button data-modal-hide="staticModal" type="button" class="text-gray-500 bg-white hover:bg-gray-100 focus:ring-4 focus:outline-none focus:ring-blue-300 rounded-lg border border-gray-200 text-sm font-medium px-5 py-2.5 hover:text-gray-900 focus:z-10 dark:bg-gray-700 dark:text-gray-300 dark:border-gray-500 dark:hover:text-white dark:hover:bg-gray-600 dark:focus:ring-gray-600">Decline</button>
            </div>
        </div>
    </div>
</div>

      </>
      ) : null}
     
    </main>
  );
};

export default Teacher;
