import React from 'react';
import { useState } from 'react';


const MaintenancePage = () => {
    const [isPressed, setIsPressed] = useState(false);

    const handleMouseDown = () => {
      setIsPressed(true);
    };
  
    const handleMouseUp = () => {
      setIsPressed(false);
    };
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-200">
      <a href="/" class="flex items-center">
            <img
              src={process.env.PUBLIC_URL + "/AVI.png"}
              alt="Logo"
              class="h-6 mr-3 sm:h-9"
            />
          </a>
      <h1 className="text-2xl md:text-4xl lg:text-5xl font-bold mb-4">PPIC & QC Production</h1>
      <p className="text-gray-700 text-base text-center md:text-xl lg:text-2xl mb-8">This Website Only For PPIC & QC "Please Input Pin To Get Into The Website"</p>
      <button
      className={`w-8 h-8 md:w-12 md:h-12 lg:w-16 lg:h-16 border-4 border-gray-900 rounded-full ${
        isPressed ? 'animate-spin' : ''
      }`}
      onMouseDown={handleMouseDown}
      onMouseUp={handleMouseUp}
    >
      {isPressed ? (
        <progress className="w-full h-full" />
      ) : null}
    </button>
    </div>
  );
};

export default MaintenancePage;
