import React, { useState, useEffect } from "react";
import firebase from "firebase/compat/app";
import "firebase/compat/database";

const firebaseConfig = {
  apiKey: "AIzaSyBF8Q3AuT5m-iBayXDt82r8RyADUdnyGsk",
  databaseURL:
    "https://classroom-1a392-default-rtdb.asia-southeast1.firebasedatabase.app",
};
firebase.initializeApp(firebaseConfig);

const database = firebase.database();


const Teacher = () => {
  const [Name, setName] = useState("");
  const [Subject, setSubject] = useState("");
  const [Timein, setTimein] = useState("");
  const [Timeout, setTimeout] = useState("");
  const [data, setData] = useState([]);
  const [OpenCaution, setOpenCaution] = useState(false);




  const [Name1, setName1] = useState("");
  const [Name2, setName2] = useState("");
  const [Name3, setName3] = useState("");
  const [Name4, setName4] = useState("");
  const [Name5, setName5] = useState("");
  const [Name6, setName6] = useState("");
  const [Name7, setName7] = useState("");
  const [Name8, setName8] = useState("");
  const [Name9, setName9] = useState("");




  // Submit PPIC / ADD
  const Add = (event) => {
    event.preventDefault();
    if (!Name || !Subject || !Timein || !Timeout) {
      alert("Harap isi semua kolom!");
      return;
    }
    const data = {
      Name: Name,
      Subject: Subject,
      Timein: Timein,
      Timeout: Timeout,
    };

    fetch(`http://localhost:3001/api/post/schedule`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => {
        if (response.status === 200) {
          alert("Schedule Succesfully Updated");
          window.location.reload();
        } else {
          throw new Error("Error adding data");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };


  useEffect(() => {
    fetch("http://localhost:3001/api/get/schedule")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
      })
      .catch((error) => {
        // Tangani error jika permintaan gagal
        console.error("Error fetching data:", error);
      });
  }, []);

  const Reset = () => {
    fetch("http://localhost:3001/api/delete/schedule", {
      method: "DELETE",
    })
      .then((response) => response.json())
      .then((data) => {
        alert("Schedule Succesfully Deleted");
        window.location.reload();
        console.log(data);
        setData([]);
      })
      .catch((error) => {
        console.error("Error resetting data:", error);
      });
  };


  useEffect(() => {
    fetch("http://localhost:3001/api/get/students")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        const foundItem = data.find(item => item.No === 1);
        if (foundItem) {
          setName1(foundItem.Name);
          const foundItem2 = data.find(item => item.No === 2);
          if (foundItem2) {
            setName2(foundItem2.Name);
          }
          const foundItem3 = data.find(item => item.No === 3);
          if (foundItem3) {
            setName3(foundItem3.Name);
          }
          const foundItem4 = data.find(item => item.No === 4);
          if (foundItem4) {
            setName4(foundItem4.Name);
          }
          const foundItem5 = data.find(item => item.No === 5);
          if (foundItem5) {
            setName5(foundItem5.Name);
          }
          const foundItem6 = data.find(item => item.No === 6);
          if (foundItem6) {
            setName6(foundItem6.Name);
          }
          const foundItem7 = data.find(item => item.No === 7);
          if (foundItem7) {
            setName7(foundItem7.Name);
          }
          const foundItem8 = data.find(item => item.No === 8);
          if (foundItem8) {
            setName8(foundItem8.Name);
          }
        }
