import {React,useState} from "react";

const MaintenancePage = () => {
    const [Password, setPassword] = useState("");
    const [isSubmitted, setIsSubmitted] = useState(false);
    const [errorCount, setErrorCount] = useState(0);
  
    const handleSubmit = (event) => {
        event.preventDefault();
        if (Password === "1234") {
          setIsSubmitted(true);
          window.location.href = "/PPICProd"; 
        } else if (Password === "4321") {
          setIsSubmitted(true);
          window.location.href = "/QCProd"; 
        } else {
            setErrorCount(errorCount + 1);
            if (errorCount === 2) {
              setIsSubmitted(true);
            }
            alert("Invalid password. Please try again.");
          }
        };
      
      const handleKeyPress = (event) => {
        if (event.key === "Enter") {
          handleSubmit(event);
        }
      };
    
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-200">
      <a href="/" class="flex items-center">
        <img
          src={process.env.PUBLIC_URL + "/AVI.png"}
          alt="Logo"
          class="h-6 mr-3 sm:h-9"
        />
      </a>
      <h1 className="text-2xl md:text-4xl lg:text-5xl font-bold mb-4">
        Planning Production Export
      </h1>
      <p className="text-gray-700 text-base text-center md:text-xl lg:text-2xl mb-8">
        This Website Only For PPIC & QC "Please Input Pin To Get Into The
        Website"
      </p>
      {!isSubmitted ? (
      <form class="w-60 max-w-sm">
        <div class="flex items-center border-b border-teal-500 py-2">
          <input
            class="appearance-none bg-transparent border-none w-full text-gray-700 mr-3 py-1 px-2 leading-tight focus:outline-none"
            type="password"
            onChange={(event) => setPassword(event.target.value)} 
            value={Password}
            onKeyDown={handleKeyPress} 
            placeholder="Masukan Pin"
          />
          <button
            class="flex-shrink-0 bg-teal-500 hover:bg-teal-700 border-teal-500 hover:border-teal-700 text-sm border-4 text-white py-1 px-2 rounded"
            type="button"
            onClick={handleSubmit}
          >
            Sign In
          </button>
        </div>
      </form>
      
          ) : (
            <div className="bg-slate-400 shadow-md rounded px-8 pt-6 pb-8 mb-4">
              <p className="text-gray-800 text-xl">Selamat Datang Kembali!!!</p>
            </div>
          )}
    </div>
  );
};

export default MaintenancePage;
